import React from 'react';
import '../css/Main/Main_carrinho.css';
/**
 * Componente de Carrinho de Compras
 * Exibe os itens, permite a mudan√ßa de quantidade e remo√ß√£o, e calcula o total.
 * * @param {object} props
 * @param {Array<object>} props.itens - A lista de produtos no carrinho.
 * @param {function} props.onRemoverItem - Fun√ß√£o para remover um item pelo ID.
 * @param {function} props.onAtualizarQuantidade - Fun√ß√£o para mudar a quantidade.
 */
function Carrinho({ itens = [], onRemoverItem, onAtualizarQuantidade }) {

  // 1. C√°lculo do Valor Total
  const total = itens.reduce((acc, item) => {
    // ‚ö†Ô∏è Importante: Remove "R$" e substitui v√≠rgula por ponto para garantir o c√°lculo correto.
    const priceString = item.price.replace('R$', '').trim().replace(',', '.');
    const priceNum = parseFloat(priceString);

    // Garante que a multiplica√ß√£o usa n√∫meros v√°lidos
    const subtotal = (isNaN(priceNum) ? 0 : priceNum) * (item.quantity || 0);
    return acc + subtotal;
  }, 0);

  return (
    <div className="carrinho-painel">
      <h2>üõí Meu Carrinho</h2>

      {itens.length === 0 ? (
        <p className="carrinho-vazio-msg">Seu carrinho est√° vazio. Comece a adicionar produtos!</p>
      ) : (
        <div className="lista-itens-carrinho">
          {itens.map(item => {
            // 2. C√°lculo do Subtotal por Item
            const priceString = item.price.replace('R$', '').trim().replace(',', '.');
            const priceNum = parseFloat(priceString);
            const subtotal = (isNaN(priceNum) ? 0 : priceNum) * (item.quantity || 0);

            return (
              <div key={item.id} className="item-carrinho-detalhe">

                {/* Imagem/Nome */}
                <div className="info-produto-carrinho">
                  {/* Se o item tiver uma imagem, voc√™ pode exibi-la aqui */}
                  {/* <img src={item.image} alt={item.name} className="item-carrinho-img" /> */}
                  <h4>**{item.name}**</h4>
                  <p>Pre√ßo Unit√°rio: {item.price}</p>
                </div>

                {/* Controles de Quantidade e Subtotal */}
                <div className="controles-carrinho">
                  <div className="quantidade-selector-carrinho">
                    <button 
                      onClick={() => onAtualizarQuantidade(item.id, item.quantity - 1)}
                      // Desabilita o bot√£o se a quantidade for 1 (para evitar quantidade 0)
                      disabled={item.quantity <= 1} 
                    >
                      -
                    </button>
                    <span className="quantidade-valor">{item.quantity}</span>
                    <button 
                      onClick={() => onAtualizarQuantidade(item.id, item.quantity + 1)}>
                      +
                    </button>
                  </div>

                  <span className="subtotal-carrinho">
                    Subtotal: **R$ {subtotal.toFixed(2).replace('.', ',')}**
                  </span>

                  <button 
                    className="btn-remover-carrinho" 
                    onClick={() => onRemoverItem(item.id)}
                  >
                    ‚ùå Remover
                  </button>
                </div>
                <hr/>
              </div>
            );
          })}
        </div>
      )}

      {/* 3. Resumo Final */}
      <div className="resumo-total-carrinho">
        <h3>Total da Compra: **R$ {total.toFixed(2).replace('.', ',')}**</h3>
        {itens.length > 0 && (
          <button className="btn-finalizar-carrinho">
            Finalizar Compra
          </button>
        )}
      </div>
    </div>
  );
}

export default Carrinho;
