import React, { useState } from 'react'; //  Adicionei o 'useState'
import Header from './componentes_principais/Navbar';
import Footer from './componentes_principais/Footer';
import Home from './componetes_secundarios/Main_home';
import Sobre from './componentes_principais/links'; 

function App() {
  // Crie o estado para rastrear a visualização ativa
  const [activeView, setActiveView] = useState('home'); // 'home' é o padrão

  // Função para mudar a visualização
  const handleNavigation = (view) => {
    setActiveView(view);
  };
    
  const renderContent = () => {
    switch (activeView) {
      case 'home':
        return <Home />;
      case 'sobre':
        
        return <Sobre />;
     
      default:
        return <Home />;
    }
  };

  return (
    <div className="body">
      {/* 3. Passe a função de navegação para o Header (Navbar) */}
      <Header onNavigate={handleNavigation} />
      
      {/* 4. Renderize apenas o conteúdo ativo */}
      {renderContent()}
      
      <Footer/>
    </div>
  );
}

export default App;
