import React, { useState, useEffect } from 'react';
import '../css/Navbar.css';
import AuthPage from '../componetes_secundarios/Login_registro'; // Importa o modal de autentica√ß√£o
import Contato from './links/contato';
import carinho from '../se';

const Header = ({ apenasLogin = false, onNavigate, isLoggedIn, onLogin, onLogout }) => {

    const [isAuthOpen, setIsAuthOpen] = useState(false);
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    // EFEITO: Controla o scroll do body para o modal
    useEffect(() => {
        if (isAuthOpen) {
            document.body.classList.add('modal-open');
        } else {
            document.body.classList.remove('modal-open');
        }
        return () => {
            document.body.classList.remove('modal-open');
        };
    }, [isAuthOpen]);

    // EFEITO: Controla a rolagem da Navbar
    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    // FUN√á√ïES DE MANUSEIO
    const handleLinkClick = (view) => {
        setIsMobileMenuOpen(false); // Fecha o menu mobile ao clicar em um link
        if (onNavigate) {
            onNavigate(view);
        }
    };

    const handleLoginClick = () => {
        setIsAuthOpen(true);
        setIsMobileMenuOpen(false); // Fecha o menu mobile se estiver aberto
    };

    // Fun√ß√£o chamada pelo AuthPage quando o login √© bem-sucedido
    const handleLoginSuccess = () => {
        setIsAuthOpen(false); // 1. Fecha o modal
        if (onLogin) {
            // 2. Chama a fun√ß√£o passada pelo App.js para atualizar o estado global
            onLogin(); 
        }
    };

    // --- L√≥gica de Renderiza√ß√£o Condicional dos Bot√µes ---
    const renderCTAs = (isMobile = false) => {
        const ctaClass = isMobile ? 'mobile-cta-button' : 'cta-button';
        const containerClass = isMobile ? 'mobile-cta' : 'navbar-cta';

        if (isLoggedIn) {
            // Se logado, mostra o bot√£o do carrinho (navbar-cta2)
            return (
                <div className={containerClass}>
                    <button 
                        id="navbar-cta2" 
                        className={ctaClass}
                        // üõí A√á√ÉO ADICIONADA: Chama a navega√ß√£o para a view 'carrinho'
                        onClick={() => handleLinkClick('carrinho')} 
                    >
                        <i className="fas fa-shopping-cart"></i> Carrinho
                    </button>
                    {isMobile && onLogout && (
                        // Adiciona bot√£o Sair no mobile
                        <button className={ctaClass} onClick={onLogout} style={{ marginTop: '10px' }}>
                            <i className="fas fa-sign-out-alt"></i> Sair
                        </button>
                    )}
                </div>
            );
        } else {
            // Se n√£o logado, mostra o bot√£o de login
            return (
                <div className={containerClass}>
                    <button id="navbar-cta" className={ctaClass} onClick={handleLoginClick}> 
                        Login 
                    </button>
                </div>
            );
        }
    };


    return (
        <>
            {/* Navbar Principal */}
            <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
                <div className="navbar-container">
                    <div className="navbar-logo">
                        <button className='navbar-logo-button' onClick={() => handleLinkClick('home')}>
                            <img src="assents/logo/logo.svg" alt="logo" className='logo' />
                            <span>Arcano Store</span>
                        </button>
                    </div>

                    {/* Menu Desktop */}
                    {!apenasLogin && (
                        <ul className="navbar-menu">
                            <li className="navbar-item"><button className="navbar-link" onClick={() => handleLinkClick('home')}>In√≠cio</button></li>
                            <li className="navbar-item"><button className="navbar-link" onClick={() => handleLinkClick('sobre')}>Sobre N√≥s</button></li>
                            <li className="navbar-item"><Contato /></li>
                        </ul>
                    )}

                    {/* Bot√µes de A√ß√£o Desktop */}
                    {renderCTAs()}

                    {/* Menu mobile toggle */}
                    {!apenasLogin && (
                        <div className={`navbar-toggle ${isMobileMenuOpen ? 'active' : ''}`} onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}>
                            <span className="bar"></span>
                            <span className="bar"></span>
                            <span className="bar"></span>
                        </div>
                    )}
                </div>
            </nav>
            {isMobileMenuOpen && (
                <div className="mobile-overlay active" onClick={() => setIsMobileMenuOpen(false)}></div>
            )}

            <div className={`mobile-menu ${isMobileMenuOpen ? 'active' : ''}`}>
                <div className="mobile-menu-header">
                    <span className="mobile-menu-title">Menu</span>
                    <button 
                        className="mobile-close-btn" 
                        onClick={() => setIsMobileMenuOpen(false)} 
                    >
                        &times;
                    </button>
                </div>
                <ul className="mobile-menu-list">
                    <li className="mobile-item">
                        <button className="mobile-link" onClick={() => handleLinkClick('home')}>
                            <i className="fas fa-home"></i> In√≠cio
                        </button>
                    </li>
                    <li className="mobile-item">
                        <button className="mobile-link" onClick={() => handleLinkClick('sobre')}>
                            <i className="fas fa-info-circle"></i> Sobre N√≥s
                        </button>
                    </li>
                    <li className="mobile-item">
                        {/* Como Contato √© um componente, ele √© renderizado diretamente */}
                        <Contato /> 
                    </li>
                </ul>

                {/* üõí Renderiza os bot√µes de a√ß√£o e inclui a navega√ß√£o para o carrinho */}
                {renderCTAs(true)}

            </div>

            {isAuthOpen && (
                <div className="modal-overlay active">
                    <AuthPage 
                        onClose={() => setIsAuthOpen(false)} 
                        onLoginSuccess={handleLoginSuccess}
                    /> 
                </div>
            )}
        </>
    );
};

export default Header;
