import React, { useState, useEffect } from 'react';

// 1. IMPORTAÇÃO CORRETA: Importe apenas a função que traz os dados
import getProdutosData, { categoriesData } from './Produtos'; 

const MainHome = () => { // Assumindo que seu componente se chama MainHome
    // 2. ESTADOS: Para guardar os produtos e o status de carregamento
    const [featuredProducts, setFeaturedProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    // As categorias podem ser importadas diretamente, pois são estáticas
    const categories = categoriesData; 

    // 3. CARREGAMENTO ASSÍNCRONO: Puxa os dados da API ao montar o componente
    useEffect(() => {
        const loadInitialData = async () => {
            setLoading(true);
            try {
                // Chama a função assíncrona para buscar os produtos e categorias
                const data = await getProdutosData();

                // Mapeamento opcional: O PHP já faz isso, mas podemos garantir.
                const mappedProducts = data.featuredProducts.map(p => ({
                    id: p.id,
                    name: p.nome,
                    price: `R$ ${parseFloat(p.preco).toFixed(2).replace('.', ',')}`, // Exemplo de formatação
                    image: p.image,
                    category: p.category,
                    discount: p.desconto > 0 ? `${p.desconto}% OFF` : null,
                    new: p.novo,
                    description: p.description
                }));

                setFeaturedProducts(mappedProducts);
            } catch (error) {
                console.error("Erro ao carregar dados da Home:", error);
                setFeaturedProducts([]); // Garante array vazio em caso de falha
            } finally {
                setLoading(false);
            }
        };

        loadInitialData();
    }, []); // Array de dependências vazio: executa apenas na montagem

    // 4. Renderização
    if (loading) {
        return <div>Carregando Produtos em Destaque...</div>;
    }

    return (
        <div className="main-home-container">
            {/* Exibindo as Categorias */}
            <h2>Nossas Categorias</h2>
            <div className="category-list">
                {categories.map(cat => (
                    <div key={cat.name} className="category-item">
                        {cat.icon} {cat.name} ({cat.count})
                    </div>
                ))}
            </div>

            {/* Exibindo os Produtos em Destaque */}
            <h2>Produtos em Destaque</h2>
            <div className="featured-products-grid">
                {featuredProducts.length > 0 ? (
                    featuredProducts.map(product => (
                        <div key={product.id} className="product-card">
                            <img src={product.image} alt={product.name} />
                            <h3>{product.name}</h3>
                            <p className="price">{product.price}</p>
                            {product.discount && <span className="discount">{product.discount}</span>}
                        </div>
                    ))
                ) : (
                    <p>Nenhum produto encontrado. Tente verificar sua API!</p>
                )}
            </div>
        </div>
    );
};

export default MainHome;
