// api_produtos.js

// Ajuste o caminho base para a localização da sua API PHP no servidor local (Apache/XAMPP)
const PHP_API_URL_BASE = '/arcanostore/backend_php/produtos_bd/produtos.php';

/**
 * Busca todos os produtos da API.
 * @returns {Promise<Array|Object>} Array de produtos ou objeto de erro.
 */
export const fetchProdutoPorId = async (produtoId) => {
    try {
        const url = `http://localhost${PHP_API_URL_BASE}?id=${produtoId}`;
        console.log("Tentando buscar produto em:", url);

        const response = await fetch(url);

        if (!response.ok) {
            // Lança um erro se a resposta HTTP não for bem-sucedida (ex: 404, 500)
            throw new Error(`Erro HTTP: ${response.status} - Verifique o caminho PHP e a resposta do servidor.`);
        }

        const data = await response.json();
        return data;

    } catch (error) {
        console.error("Erro ao buscar produtos:", error);
        // Retorna um objeto de erro padronizado para ser tratado no frontend
        return { sucesso: false, mensagem: "Erro ao buscar produtos. Verifique se o XAMPP/WAMP está ligado e a URL." }; 
    }
}

// Funções fetchProdutoPorId e buscarProdutosPorCategoria omitidas para simplificação, mas usariam a mesma URL base.

// Exporte outras funções, como categoriesData, se necessário para seu sistema de dados.
