import React from 'react';
import './carrinho_home.css';
/**
 * @param {object} props
 * @param {Array<object>} props.itens - A lista de produtos no carrinho.
 * @param {function} props.onRemoverItem - Fun√ß√£o para remover um item do carrinho.
 * @param {function} props.onAtualizarQuantidade - Fun√ß√£o para mudar a quantidade.
 */
function Carrinho({ itens, onRemoverItem, onAtualizarQuantidade }) {
  // Calcula o valor total do carrinho
  const total = itens.reduce((acc, item) => {
    // √â importante converter o pre√ßo de volta para n√∫mero se voc√™ o armazenou como string formatada
    const priceNum = parseFloat(item.price.replace('R$', '').replace(',', '.'));
    return acc + (priceNum * item.quantity);
  }, 0);

  return (
    <div className="carrinho-painel">
      <h2>üõí Meu Carrinho</h2>

      {itens.length === 0 ? (
        <p className="carrinho-vazio-msg">Seu carrinho est√° vazio.</p>
      ) : (
        <div className="lista-itens-carrinho">
          {itens.map(item => {
            const priceNum = parseFloat(item.price.replace('R$', '').replace(',', '.'));
            const subtotal = priceNum * item.quantity;

            return (
              <div key={item.id} className="item-carrinho-detalhe">

                {/* Informa√ß√µes do Produto */}
                <div className="info-produto-carrinho">
                  <h4>{item.name}</h4>
                  <p>Pre√ßo: {item.price}</p>
                </div>

                {/* Controles de Quantidade */}
                <div className="controles-carrinho">
                  <div className="quantidade-selector-carrinho">
                    <button onClick={() => onAtualizarQuantidade(item.id, item.quantity - 1)}>-</button>
                    <span className="quantidade-valor">{item.quantity}</span>
                    <button onClick={() => onAtualizarQuantidade(item.id, item.quantity + 1)}>+</button>
                  </div>

                  <span className="subtotal-carrinho">
                    Subtotal: R$ {subtotal.toFixed(2).replace('.', ',')}
                  </span>

                  <button 
                    className="btn-remover-carrinho" 
                    onClick={() => onRemoverItem(item.id)}
                  >
                    Remover
                  </button>
                </div>

                <hr/>
              </div>
            );
          })}
        </div>
      )}

      <div className="resumo-total-carrinho">
        <h3>Total da Compra: R$ {total.toFixed(2).replace('.', ',')}</h3>
        {itens.length > 0 && (
          <button className="btn-finalizar-carrinho">
            Finalizar Compra
          </button>
        )}
      </div>
    </div>
  );
}

export default Carrinho;
