import React, { useState, useEffect } from 'react';
import '../css/Navbar.css';
import AuthPage from '../componetes_secundarios/login_registro'; // Garanta que este caminho sempre vai ficar correto
import Contato from './links/contato';

const Navbar = ({ apenasLogin = false }) => {
    const [isAuthOpen, setIsAuthOpen] = useState(false);
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

    // EFEITO: Controla o scroll do body para o modal
    useEffect(() => {
        if (isAuthOpen) {
            document.body.classList.add('modal-open');
        } else {
            document.body.classList.remove('modal-open');
        }
        return () => {
            document.body.classList.remove('modal-open');
        };
    }, [isAuthOpen]);

    // EFEITO: Controla a rolagem da Navbar
    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 50);
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    // FUNÃ‡Ã•ES DE MANUSEIO
    const handleLinkClick = (view) => { // ðŸ‘ˆ Adicione 'view' aqui
        setIsMobileMenuOpen(false);
        // Chama a funÃ§Ã£o onNavigate (precisamos do prop no Navbar)
        if (onNavigate) {
            onNavigate(view); 
        }
    };

    const handleLoginClick = () => {
        setIsAuthOpen(true);
        setIsMobileMenuOpen(false); // Boa prÃ¡tica: fechar o menu mobile ao abrir o modal
    };

    

    return (
        <>
            {/* Navbar Principal */}
            <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
                <div className="navbar-container">

                    {/* Logo */}
                    <div className="navbar-logo">
                        <a href="." onClick={handleLinkClick}>
                            <img src="assents/logo/logo.svg" alt="logo" className='logo' />
                            <span>Arcano Store</span>
                        </a>
                    </div>

                    {/* Menu Desktop */}
                    {!apenasLogin && (
                        <ul className="navbar-menu">
                            <li className="navbar-item"><a href="#" className="navbar-link" onClick={() => handleLinkClick('home')}>InÃ­cio</a></li>
                            <li className="navbar-item"><a href="#" className="navbar-link" onClick={() => handleLinkClick('sobre')}>Sobre NÃ³s</a></li> 
                            <li className="navbar-item"><Contato /></li>
                        </ul>
                    )}

                    {/* BotÃ£o de Login Desktop */}
                    <div className="navbar-cta"> {/* Envolva o botÃ£o CTA em uma div para manter o alinhamento CSS */}
                        <button className="cta-button" onClick={handleLoginClick}> Login </button>
                    </div>

                    {/* Menu mobile toggle */}
                    {!apenasLogin && (
                        <div
                            className={`navbar-toggle ${isMobileMenuOpen ? 'active' : ''}`}
                            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                        >
                            <span className="bar"></span>
                            <span className="bar"></span>
                            <span className="bar"></span>
                        </div>
                    )}
                </div>

                {/* Menu mobile (Renderizado dentro da nav, mas fora do container) */}
                {!apenasLogin && (
                    <div className={`mobile-menu ${isMobileMenuOpen ? 'active' : ''}`}>
                        <ul className="mobile-menu-list">
                            {/* ... Seus itens de menu mobile aqui ... */}
                            <li className="mobile-item"><a href="#home" className="mobile-link" onClick={handleLinkClick}><i className="fas fa-home"></i>InÃ­cio</a></li>
                            <li className="mobile-item"><a href="#about" className="mobile-link" onClick={handleLinkClick}><i className="fas fa-user"></i>Sobre</a></li>
                            <li className="mobile-item"><a href="#services" className="mobile-link" onClick={handleLinkClick}><i className="fas fa-cog"></i>ServiÃ§os</a></li>
                            <li className="mobile-item"><a href="#portfolio" className="mobile-link" onClick={handleLinkClick}><i className="fas fa-briefcase"></i>PortfÃ³lio</a></li>
                            <li className="mobile-item"><a href="#contact" className="mobile-link" onClick={handleLinkClick}><i className="fas fa-envelope"></i>Contato</a></li>
                        </ul>
                        {/* BotÃ£o de Login no CTA Mobile */}
                        <div className="mobile-cta">
                            <button className="mobile-cta-button" onClick={handleLoginClick}>
                                <i className="fas fa-sign-in-alt"></i> Entrar
                            </button>
                        </div>
                    </div>
                )}
            </nav>

            {/* O MODAL DE AUTENTICAÃ‡ÃƒO */}
            {isAuthOpen && (
                <div className="modal-overlay active"> {/* Adicione 'active' aqui para o CSS funcionar */}
                    <AuthPage />
                </div>
            )}
        </>
    );
};

export default Navbar;
