// Componente para o formulário de Login
const LoginForm = () => {
    return (
        <form action="db_login.php" method="post"> {/* Alterei para POST, que é mais seguro para login */}
            <h1>Login</h1>
            <label htmlFor="login-username-email">Nome ou Email</label>
            <input type="text" id="login-username-email" placeholder="Digite seu nome ou email:" name="username_or_email" required />

            <label htmlFor="login-password">Senha</label>
            <input type="password" id="login-password" placeholder="Digite sua senha:" name="password" required />

            <input type="submit" value="Entrar" />
        </form>
    );
};

// Componente para o formulário de Registro

const AuthPage = () => {
    const [isLogin, setIsLogin] = useState(true);

    return (
        <div className="login_Registro">
            <div className="toggle-buttons">
                <button 
                    onClick={() => setIsLogin(true)} 
                    style={{ fontWeight: isLogin ? 'bold' : 'normal' }}
                >
                    Login
                </button>
                <button 
                    onClick={() => setIsLogin(false)} 
                    style={{ fontWeight: !isLogin ? 'bold' : 'normal' }}
                >
                    Registro
                </button>
            </div>
            {isLogin ? <LoginForm /> : <RegistrationForm />}
        </div>
    );
};

export default AuthPage;

import '../css/Registro/login.css';
import React, { useState } from 'react';
// IMPORTA a função de comunicação com o PHP/API
import { registrarUsuario } from './services/apiService'; 

// Componente para o formulário de Registro (AGORA CONTROLADO PELO REACT)
const RegistrationForm = () => {
    // 1. Usa o estado para gerenciar todos os campos do formulário
    const [formData, setFormData] = useState({
        full_name: '', email: '', date_nas: '', 
        cpf: '', telefone: '', password: '', 
        confirm_password: ''
    });
    const [statusMensagem, setStatusMensagem] = useState('');
    const [loading, setLoading] = useState(false);

    // 2. Função genérica para atualizar o estado quando o usuário digita
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    // 3. Função para lidar com a submissão do formulário
    const handleSubmit = async (e) => {
        e.preventDefault(); // <-- IMPEDE o comportamento padrão HTML (SAIR DA PÁGINA)

        // Verifica se as senhas coincidem ANTES de enviar
        if (formData.password !== formData.confirm_password) {
            setStatusMensagem("As senhas não coincidem!");
            return;
        }

        setStatusMensagem('Enviando dados...');
        setLoading(true);

        // 4. CHAMA a função da API (que faz o fetch/POST para o PHP)
        const resultado = await registrarUsuario(formData);

        // 5. Trata o resultado do PHP
        if (resultado.sucesso) {
            setStatusMensagem(`Sucesso: ${resultado.mensagem}. Você será redirecionado.`);
            // Opcional: Limpar formulário ou redirecionar
            setFormData({ // Limpa os campos
                full_name: '', email: '', date_nas: '', 
                cpf: '', telefone: '', password: '', 
                confirm_password: ''
            });
        } else {
            setStatusMensagem(`Erro: ${resultado.mensagem}`);
        }
        setLoading(false);
    };

    return (
        <form onSubmit={handleSubmit}> {/* Passa o manipulador de submissão do React */}
            <h1>Registro</h1>
            {/* Todos os inputs agora usam 'name', 'value' do estado e 'onChange' */}

            <label htmlFor="register-name">Nome Completo</label>
            <input type="text" id="register-name" placeholder="Digite seu nome completo:" name="full_name" value={formData.full_name} onChange={handleChange} required />

            <label htmlFor="register-email">Email</label>
            <input type="email" id="register-email" placeholder="Digite seu email:" name="email" value={formData.email} onChange={handleChange} required />

            <label htmlFor="register-dataNascimento">Data de Nascimento</label>
            <input type="date" id="register-dataNascimento" name='date_nas' value={formData.date_nas} onChange={handleChange} required/>

            {/* ... Repita o padrão (name, value, onChange) para todos os outros inputs ... */}

            <label htmlFor="register-cpf">CPF</label>
            <input type="text" id="register-cpf" placeholder="Digite seu cpf:" name="cpf" value={formData.cpf} onChange={handleChange} required />

            <label htmlFor="register-telefone">Telefone</label>
            <input type="text" id="register-telefone" placeholder="Digite seu numero de telefone:" name="telefone" value={formData.telefone} onChange={handleChange} required /> 

            <label htmlFor="register-password">Senha</label>
            <input type="password" id="register-password" placeholder="Digite uma senha:" name="password" value={formData.password} onChange={handleChange} required />

            <label htmlFor="register-confirm-password">Confirmação de Senha</label>
            <input type="password" id="register-confirm-password" placeholder="Confirme sua senha:" name="confirm_password" value={formData.confirm_password} onChange={handleChange} required />

            <input type="submit" value={loading ? "Carregando..." : "Cadastrar"} disabled={loading} />

            {statusMensagem && <p className={resultado.sucesso ? 'success' : 'error'}>{statusMensagem}</p>}
        </form>
    );
};
