// **ATENÇÃO:** A URL deve ser absoluta a partir do seu servidor web (Apache/XAMPP)
// **ASSUMA** que seu servidor web está servindo a pasta 'backend_php' na raiz.
c// --- MUDANÇA RECOMENDADA PARA REGISTRO ---
const API_BASE_URL = 'http://localhost'; // ou 'http://localhost:8080' se for o caso
// O PHP_API_URL_REGISTRO permanece o mesmo se a pasta 'backend_php' estiver na raiz do servidor web
const PHP_API_URL_REGISTRO = '/backend_php/login_registro/registro.php';
// --- FUNÇÃO DE REGISTRO ---
export const registrarUsuario = async (userData) => {
    try {
        const response = await fetch(`${API_BASE_URL}${PHP_API_URL_REGISTRO}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData), 
        });

        // response.ok só será true para status 200-299. O 201 (Created) do PHP é pego aqui.
        if (!response.ok) {
            // Se o PHP retornou um erro (400, 500, etc.), tentamos ler o JSON de erro
            const errorText = await response.text(); // Tentamos ler como texto primeiro
            let errorMessage = `Erro HTTP: ${response.status}`;
            try {
                 const errorData = JSON.parse(errorText); // Tenta parsear como JSON
                 errorMessage = errorData.mensagem || errorMessage;
            } catch (e) {
                // Se não for JSON, mantemos a mensagem de status.
            }
            throw new Error(errorMessage);
        }
        return await response.json(); 

    } catch (error) {
        // Aqui pega erros de rede (Failed to fetch) ou erros que lançamos (HTTP status não OK)
        return { sucesso: false, mensagem: error.message || "Erro de conexão com o servidor de registro." };
    }
};

// --- FUNÇÃO DE LOGIN (Sugestão para o LoginForm) ---
export const fazerLogin = async (loginData) => {
    try {
        const response = await fetch(PHP_API_URL_LOGIN, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(loginData), 
        });

        if (!response.ok) {
            const errorText = await response.text();
            let errorMessage = `Erro HTTP: ${response.status}`;
            try {
                 const errorData = JSON.parse(errorText);
                 errorMessage = errorData.mensagem || errorMessage;
            } catch (e) {
                // Se não for JSON, mantemos a mensagem de status.
            }
            throw new Error(errorMessage);
        }
        return await response.json(); 

    } catch (error) {
        console.error("Erro na comunicação com o Backend de Login:", error);
        return { sucesso: false, mensagem: error.message || "Erro de conexão com o servidor de login." };
    }
}
