import React, { useState } from 'react'; // üëà Adicione o 'useState'
import Header from './componentes_principais/Navbar';
import Footer from './componentes_principais/Footer';
import Home from './componetes_secundarios/Main_home';
import Sobre from './links/sobrenos'; 

function App() {
  // 1. Crie o estado para rastrear a visualiza√ß√£o ativa
  const [activeView, setActiveView] = useState('home'); // 'home' √© o padr√£o

  // Fun√ß√£o para mudar a visualiza√ß√£o
  const handleNavigation = (view) => {
    setActiveView(view);
  };
    
  // 2. Fun√ß√£o para renderizar o conte√∫do condicionalmente
  const renderContent = () => {
    switch (activeView) {
      case 'home':
        return <Home />;
      case 'sobre':
        // Assumindo que 'Sobre' √© o componente que voc√™ forneceu no c√≥digo
        return <Sobre />;
      // Adicione outras rotas se necess√°rio (ex: 'contato')
      default:
        return <Home />;
    }
  };

  return (
    <div className="body">
      {/* 3. Passe a fun√ß√£o de navega√ß√£o para o Header (Navbar) */}
      <Header onNavigate={handleNavigation} />
      
      {/* 4. Renderize apenas o conte√∫do ativo */}
      {renderContent()}
      
      <Footer/>
    </div>
  );
}

export default App;
