import React, { useState, useEffect } from 'react';
import '../css/Navbar.css';
import AuthPage from '../componente_sistema/login_registro';

// 1. O componente agora recebe a prop 'apenasLogin'
const Navbar = ({ apenasLogin = false }) => {
  const [isAuthOpen, setIsAuthOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLinkClick = () => {
    setIsMobileMenuOpen(false);
  };
  const handleLoginClick = () => {
    setIsAuthOpen(true);
  };// Dentro do componente Navbar
  useEffect(() => {
      // Controla o scroll do body quando o modal estÃ¡ aberto
      if (isAuthOpen) {
          document.body.classList.add('modal-open');
      } else {
          document.body.classList.remove('modal-open');
      }
      // Opcional: Adiciona a limpeza do body no unmount
      return () => {
          document.body.classList.remove('modal-open');
      };
  }, [isAuthOpen]); // Depende do estado do modal

  return (
    <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        {/* Logo (Sempre VisÃ­vel) */}
        <div className="navbar-logo">
          <a href="." onClick={handleLinkClick}>
            <img src="assents/logo/logo.svg" alt="logo" className='logo'/>
            <span>Arcano Store</span>
          </a>
        </div>

        {/* 2. Menu para desktop - RenderizaÃ§Ã£o Condicional */}
        {!apenasLogin && (
          <ul className="navbar-menu">
            <li className="navbar-item">
              <a href="#home" className="navbar-link">InÃ­cio</a>
            </li>
            <li className="navbar-item">
              <a href="#about" className="navbar-link">Sobre</a>
            </li>
            <li className="navbar-item">
              <a href="#services" className="navbar-link">ServiÃ§os</a>
            </li>
            <li className="navbar-item">
              <a href="#portfolio" className="navbar-link">PortfÃ³lio</a>
            </li>
            <li className="navbar-item">
              <a href="#contact" className="navbar-link">Contato</a>
            </li>
          </ul>
        )}

        {/* BotÃ£o de aÃ§Ã£o login (Sempre VisÃ­vel) */}
        <button 
          className="cta-button" 
          onClick={handleLoginClick}
        >
          Login
        </button>

        {/* Renderiza o Modal de AutenticaÃ§Ã£o */}
        {isAuthOpen && (
          <div className="modal-overlay">
            <AuthPage />
            <button className="close-btn" onClick={() => setIsAuthOpen(false)}>X</button>
          </div>
        )}

        {/* 3. Menu mobile toggle (Somente se nÃ£o for apenas login) */}
        {!apenasLogin && (
            <div 
              className={`navbar-toggle ${isMobileMenuOpen ? 'active' : ''}`}
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            >
              <span className="bar"></span>
              <span className="bar"></span>
              <span className="bar"></span>
            </div>
        )}

      </div>

      {/* 4. Menu mobile - RenderizaÃ§Ã£o Condicional */}
      {!apenasLogin && (
        <div className={`mobile-menu ${isMobileMenuOpen ? 'active' : ''}`}>
          <ul className="mobile-menu-list">
            <li className="mobile-item">
              <a href="#home" className="mobile-link" onClick={handleLinkClick}>
                <i className="fas fa-home"></i>
                InÃ­cio
              </a>
            </li>
            <li className="mobile-item">
              <a href="#about" className="mobile-link" onClick={handleLinkClick}>
                <i className="fas fa-user"></i>
                Sobre
              </a>
            </li>
            <li className="mobile-item">
              <a href="#services" className="mobile-link" onClick={handleLinkClick}>
                <i className="fas fa-cog"></i>
                ServiÃ§os
              </a>
            </li>
            <li className="mobile-item">
              <a href="#portfolio" className="mobile-link" onClick={handleLinkClick}>
                <i className="fas fa-briefcase"></i>
                PortfÃ³lio
              </a>
            </li>
            <li className="mobile-item">
              <a href="#contact" className="mobile-link" onClick={handleLinkClick}>
                <i className="fas fa-envelope"></i>
                Contato
              </a>
            </li>
            {/* 5. REMOVIDO o botÃ£o de login repetido no menu mobile, 
                pois jÃ¡ existe um CTA principal no navbar-container. 
                Se vocÃª realmente quiser mantÃª-lo aqui, veja o ajuste abaixo. 
                
                AJUSTE: Para manter o botÃ£o de Login no menu mobile:
                <li className="mobile-item">
                  <button className="cta-button" onClick={handleLoginClick}>Login</button>
                </li>
            */}
          </ul>
        </div>
      )}
      
      {/* 6. CORREÃÃO: O modal-overlay deve ficar fora do <nav> 
          ou pelo menos fora do escopo de <a href> e <ul>
          para evitar problemas de layout e acessibilidade. 
          VocÃª jÃ¡ tinha ele no navbar-container, entÃ£o este ponto Ã© apenas observaÃ§Ã£o.
      */}
    </nav>
  );
};

export default Navbar;
